import { useContext, useEffect, useState } from "react";

import React from "react";
import CartIcon from "../Cart/CartIcon";
import CartContext from "../../store/cart-context";

import classes from "./HeaderCartButton.module.css";

const HeaderCartButton = (props) => {
  [btnIsHighlighted, setBtnIsHighlighted] = useState(false);
  //przez to ze uzywamy kontekstu, caly nasz komponent HeaderCartButton bedzie "re-evaluated" przez React za kazdym razem gdy kontekst ulegnie zmianie (czyli gdy ulegnie zmianie w CartProvider.js bo tam mamy state)
  const cartCtx = useContext(CartContext);

  //uzywamy .reduce zeby obliczyc ilosc produktow w koszyku -> przechowujemy jeden typ np. "sushi" i w property amount ilosc tego produktu, stad:
  const numberOfCartItems = cartCtx.items.reduce((curNumber, item) => {
    return curNumber + item.amount;
  }, 0);

  //przyklad wykorzystania useEffect aby dodac animacje do guzika i po uplywie pewnego czasu usunac klase z ta animacja

  const btnClasses = `${classes.button} ${
    btnIsHighlighted ? classes.bump : ""
  }`;

  useEffect(() => {
    if (cartCtx.length === 0) {
      return;
    }
    setBtnIsHighlighted(true);

    return;
  }, [cartCtx]);

  return (
    <button className={btnClasses} onClick={props.onClick}>
      <span className={classes.icon}>
        <CartIcon />
      </span>
      <span>Tw√≥j koszyk</span>
      <span className={classes.badge}>{numberOfCartItems}</span>
    </button>
  );
};

export default HeaderCartButton;
