import { useEffect, useState } from "react";

import Card from "../UI/Cards";
import MealItem from "./MealItem/MealItem";

import classes from "./AvailableMeals.module.css";
//Zamieniamy dummy tablice na dance przechowane w Firebase
// const DUMMY_MEALS = [
//   {
//     id: "m1",
//     name: "Sushi",
//     description: "Ryba najwyższej jakości",
//     price: 22.99,
//   },
//   {
//     id: "m2",
//     name: "Schnitzel",
//     description: "Niemiecki specjał",
//     price: 16.5,
//   },
//   {
//     id: "m3",
//     name: "Burger Barbecue",
//     description: "Wołowy burger z sosem BBQ",
//     price: 12.99,
//   },
//   {
//     id: "m4",
//     name: "Zielona miska",
//     description: "Świeże warzywa - samo zdrowie",
//     price: 18.99,
//   },
// ];

//nie powinnismy uzywac funkcji asynchronicznej jako argumentu useEffect (zwracamy cleanup function, ktora musi byc synchroniczna a nie jak to robia funkcje async zwracac Promise), zamiast tego tworzymy asynchroniczna funkcje wewnatrz tej funckji i ja wywolujemy linijke nizej
const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  //poczatkowo nie mamy zadnych danych, po sciagnieciu ich przy uzyciu useEffect musimy przeladowac nasz komponent - zatem potrzebujemy statu, aby przekazac sciagniete dane i przeladowac komponent...
  useEffect(() => {
    const fetchMeals = async () => {
      setIsLoading(true);
      const response = await fetch(
        "https://test1-76ad3-default-rtdb.firebaseio.com/meals.json"
      );
      const responseData = await response.json();
      // Firebase zwraca nam obiekt meals -> a my chcemy tablice:
      const loadedMeals = [];
      //przechodzimy przez wszystkie klucze obiektu meals (m1, m2, m3, m4) i wstawiamy znajdujace sie pod nimi wartosci (wlasciwie obiekty dań) poprzez odwolanie sie do meals[m1], meals[m2] itd. do tablicy loadedMeals.
      for (key in responseData) {
        loadedMeals.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price,
        });
      }
      setIsLoading(false);
      setMeals(loadedMeals);
    };
    fetchMeals();
  }, []);

  const mealsList = meals.map((meal) => (
    <MealItem
      id={meal.id}
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
